import groq from 'groq'
import imageUrlBuilder from '@sanity/image-url'
import client from 'C:/Users/sunilkumara.v.j/Desktop/NextjsSanity/client.js'

function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}

const Post = (props) => {
  const {
    title,
    price,
    description,
    image
  } = props
  return (
    <article>
      <h1>{title}</h1>
      <span>By {name}</span>
      {categories && (
        <ul>
          Posted in
          {categories.map(category => <li key={category}>{category}</li>)}
        </ul>
      )}
      {image && (
        <div>
          <img
            src={urlFor(image)
              .width(50)
              .url()}
          />
        </div>
      )}
    </article>
  )
}

const query = groq`*[_type == "beerProducts" && slug.current == $slug][0]{
  price,
  title,
  description,
  "image": asset->url,
  
}`

Post.getInitialProps = async function (context) {
  // It's important to default the slug so that it doesn't return "undefined"
  const { slug = "" } = context.query
  return await client.fetch(query, {
    slug
  })
}

export default Post
